package javahub;

public class Automatic {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n =77,s=0,t1=n;
		s=n*n;//5776
		int a = s%100;//76
		if(a==t1)
			System.out.println(t1 + " is a automatic number");
		else
			System.out.println(t1 + " is not automatic number");
	}

}
